targets.expr:
sum(
  rate(
    nginx_ingress_controller_requests_total{status!~"[4-5].*", controller_class=~"$controller_class", ingress=~"$ingress", namespace=~"$namespace", controller_pod=~"$pod"}[${__range_s}s]
      )
   )   
/ 
(
  sum(
    rate(
      nginx_ingress_controller_requests_total{ controller_class=~"$controller_class", ingress=~"$ingress", namespace=~"$namespace", controller_pod=~"$pod"}[${__range_s}s]
        )
     ) - 
  (
  sum(
    rate(
      nginx_ingress_controller_requests_total{status=~"404|499", controller_class=~"$controller_class", ingress=~"$ingress",namespace=~"$namespace", controller_pod=~"$pod"}[${__range_s}s]
        )
     ) 
  or vector(0)
  )
)

sum(
  rate(
    nginx_ingress_controller_requests_total{status!~"[4-5].*", controller_class=~"$controller_class", ingress=~"$ingress", namespace=~"$namespace", controller_pod=~"$pod"}[${__range_s}s]
      )
   ) by (ingress)
/ 
(
  sum(
    rate(
      nginx_ingress_controller_requests_total{ controller_class=~"$controller_class", ingress=~"$ingress", namespace=~"$namespace", controller_pod=~"$pod"}[${__range_s}s]
        )
     ) by (ingress)
     - 
  (
  sum(
    rate(
      nginx_ingress_controller_requests_total_total{status=~"404|499", controller_class=~"$controller_class", ingress=~"$ingress",namespace=~"$namespace", controller_pod=~"$pod"}[${__range_s}s]
        )
     ) by (ingress)
  or vector(0)
  )
)
